////Problem statement -->Java Program to Display Even Numbers From 1 to 100
package JavaNumberProgram;

public class OddNumberDisplay {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i =1; i<=100; i++) {
			if(i%2 != 0) {
				System.out.print(i + " ");
			}
		}

	}

}
